import React from "react";

function App() {
  const [open, setOpen] = React.useState(false);
  const [step, setStep] = React.useState(1);
  const [answer1, setAnswer1] = React.useState(null);
  const [answer2, setAnswer2] = React.useState(null);

  return (
    <div className="App">
      <button onClick={() => setOpen(true)} className="open-modal-btn">
        Открыть опрос
      </button>

      <div className={`overlay animated ${open ? "show" : ""}`}>
        <div className="modal">
          <svg
            className="image"
            onClick={() => {
              setOpen(false);
              setStep(1);
              setAnswer1(null);
              setAnswer2(null);
            }}
            width="18"
            height="20"
            fill="currentColor"
          >
            <use href="/images/delete-icon.svg#trash" />
          </svg>

          <div style={{ textAlign: "center", padding: "20px" }}>
            {step === 1 && (
              <>
                <p style={{ marginBottom: "20px" }}>1. React — это круто?</p>
                <button 
                  style={btnStyle} 
                  onClick={() => {
                    setAnswer1("Да");
                    setStep(2);
                  }}
                >
                  Да
                </button>
                <button 
                  style={btnStyle} 
                  onClick={() => {
                    setAnswer1("Нет");
                    setStep(2);
                  }}
                >
                  Нет
                </button>
              </>
            )}
            
            {step === 2 && (
              <>
                <p style={{ marginBottom: "20px" }}>2. Хуки — удобны?</p>
                <button 
                  style={btnStyle} 
                  onClick={() => {
                    setAnswer2("Да");
                    setStep(3);
                  }}
                >
                  Да
                </button>
                <button 
                  style={btnStyle} 
                  onClick={() => {
                    setAnswer2("Нет");
                    setStep(3);
                  }}
                >
                  Нет
                </button>
              </>
            )}
            
            {step === 3 && (
              <>
                <h3>Ваши ответы:</h3>
                <p>1. React — это круто? - {answer1}</p>
                <p>2. Хуки — удобны? - {answer2}</p>
                
                <button 
                  style={{ ...btnStyle, marginTop: "20px" }} 
                  onClick={() => setOpen(false)}
                >
                  Закрыть
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// Стили кнопок
const btnStyle = {
  padding: "10px 20px",
  margin: "0 5px",
  border: "2px solid #333",
  borderRadius: "5px",
  cursor: "pointer",
  backgroundColor: "#f0f0f0",
  fontSize: "16px"
};

export default App;
