import React from "react";

function App() {
  const [open, setOpen] = React.useState(false);
  const [answer, setAnswer] = React.useState(null);

  return (
    <div className="App">
      <button onClick={() => setOpen(true)} className="open-modal-btn">
        Открыть опрос
      </button>

      <div className={`overlay animated ${open ? "show" : ""}`}>
        <div className="modal">
          <svg
            className="image"
            onClick={() => setOpen(false)}
            width="18"
            height="20"
            fill="currentColor"
          >
            <use href="/images/delete-icon.svg#trash" />
          </svg>

          <div className="quiz">
            <h3>Нравится ли вам React?</h3>
            <div className="buttons">
              <button onClick={() => setAnswer('Да')}>Да</button>
              <button onClick={() => setAnswer('Нет')}>Нет</button>
            </div>
            {answer && <p>Ваш ответ: {answer}</p>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
