import React from "react";

function App() {
  const [open, setOpen] = React.useState(false);
  const [step, setStep] = React.useState(1);
  const [answer1, setAnswer1] = React.useState(null);
  const [answer2, setAnswer2] = React.useState(null);

  return (
    <div className="App">
      <button onClick={() => setOpen(true)} className="open-modal-btn">
        Открыть опрос
      </button>

      <div className={`overlay animated ${open ? "show" : ""}`}>
        <div className="modal">
          <svg
            className="image"
            onClick={() => setOpen(false)}
            width="18"
            height="20"
            fill="currentColor"
          >
            <use href="/images/delete-icon.svg#trash" />
          </svg>

          <div className="quiz-content">
            {step === 1 && (
              <>
                <p className="question-step">1. React — это круто?</p>
                <div className="options">
                  <button
                    className="btn-option"
                    onClick={() => {
                      setAnswer1("Да");
                      setStep(2);
                    }}
                  >
                    Да
                  </button>
                  <button
                    className="btn-option"
                    onClick={() => {
                      setAnswer1("Нет");
                      setStep(2);
                    }}
                  >
                    Нет
                  </button>
                </div>
              </>
            )}

            {step === 2 && (
              <>
                <p className="question-step">2. Хуки — удобны?</p>
                <div className="options">
                  <button
                    className="btn-option"
                    onClick={() => {
                      setAnswer2("Да");
                      setStep(3);
                    }}
                  >
                    Да
                  </button>
                  <button
                    className="btn-option"
                    onClick={() => {
                      setAnswer2("Нет");
                      setStep(3);
                    }}
                  >
                    Нет
                  </button>
                </div>
              </>
            )}

            {step === 3 && (
              <div className="results">
                <h3>
                  Результаты:{" "}
                  {answer1 === "Да" && answer2 === "Да"
                    ? "✅ Верно!"
                    : "❌ Есть ошибки"}
                </h3>
                <p>
                  1. React — это круто? - {answer1}{" "}
                  {answer1 === "Да" ? "✅" : "❌"}
                </p>
                <p>
                  2. Хуки — удобны? - {answer2} {answer2 === "Да" ? "✅" : "❌"}
                </p>
              </div>
            )}
            <button 
                  className="btn-close" 
                  onClick={() => setOpen(false)}
                >
                  Закрыть
                </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
