import React from "react";

function App() {
  const [open, setOpen] = React.useState(false);
  const [currentQuestion, setCurrentQuestion] = React.useState(0);
  const [score, setScore] = React.useState(0);

  const questions = [
    {
      question: "Нравится ли вам React?",
      options: ["Да", "Нет"],
      correct: 0 // Индекс правильного ответа (0 = "Да")
    },
    {
      question: "Используете ли вы хуки?",
      options: ["Да", "Нет", "Иногда"],
      correct: 0
    }
  ];

  const handleAnswer = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].correct) {
      setScore(score + 1);
    }

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      alert(`Тест завершен! Ваш результат: ${score}/${questions.length}`);
      setOpen(false);
    }
  };

  return (
    <div className="App">
      <button onClick={() => setOpen(true)} className="open-modal-btn">
        Открыть опросник
      </button>
      <div className={`overlay animated ${open ? "show" : ""}`}>
        <div className="modal">
          <svg
            className="image"
            onClick={() => setOpen(false)}
            width="18"
            height="20"
            fill="currentColor"
          >
            <use href="/images/delete-icon.svg#trash" />
          </svg>

          <div className="quiz">
            <h3>{questions[currentQuestion].question}</h3>
            <div className="options">
              {questions[currentQuestion].options.map((option, index) => (
                <button
                  key={index}
                  onClick={() => handleAnswer(index)}
                  className="option-btn"
                >
                  {option}
                </button>
              ))}
            </div>
            <div className="progress">
              Вопрос {currentQuestion + 1} из {questions.length}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
