import React from "react";

function App() {
  const [open, setOpen] = React.useState(false);
  const [step, setStep] = React.useState(1);
  const [result, setResult] = React.useState(null);

  const handleAnswer = (answer) => {
    if (step === 1) {
      setStep(2);
    } else {
      setResult(answer === "Да" ? "Вы оптимист!" : "Вы реалист!");
      setStep(3);
    }
  };

  const resetTest = () => {
    setOpen(false);
    setStep(1);
    setResult(null);
  };

  return (
    <div className="App">
      <button onClick={() => setOpen(true)} className="open-modal-btn">
        Открыть опрос
      </button>

      <div className={`overlay animated ${open ? "show" : ""}`}>
        <div className="modal">
          <svg
            className="image"
            onClick={resetTest}
            width="18"
            height="20"
            fill="currentColor"
          >
            <use href="/images/delete-icon.svg#trash" />
          </svg>

          <div className="quiz-content" style={{ textAlign: "center", padding: "20px" }}>
            {step === 1 && (
              <>
                <p style={{ marginBottom: "20px" }}>React — это круто?</p>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer("Да")}
                >
                  Да
                </button>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer("Нет")}
                >
                  Нет
                </button>
              </>
            )}
            
            {step === 2 && (
              <>
                <p style={{ marginBottom: "20px" }}>Хуки — удобны?</p>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer("Да")}
                >
                  Да
                </button>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer("Нет")}
                >
                  Нет
                </button>
              </>
            )}
            
            {step === 3 && (
              <>
                <h3>Результат:</h3>
                <p style={{ margin: "20px 0" }}>{result}</p>
                <button 
                  style={{ ...btnStyle, backgroundColor: "#4CAF50" }} 
                  onClick={resetTest}
                >
                  Закрыть
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
