import React from 'react';

function App() {
  const [open, setOpen] = React.useState(false);
  const [score, setScore] = React.useState(0);
  const [currentQuestion, setCurrentQuestion] = React.useState(0);

  const questions = [
    {
      question: "React - это библиотека или фреймворк?",
      options: ["Библиотека", "Фреймворк", "Язык программирования"],
      correct: 0
    },
    {
      question: "Какой хук используется для состояния?",
      options: ["useEffect", "useState", "useContext"],
      correct: 1
    }
  ];

  const handleAnswer = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].correct) {
      setScore(score + 1);
    }
    
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      alert(`Ваш результат: ${score + 1}/${questions.length}`);
      setOpen(false);
      setCurrentQuestion(0);
      setScore(0);
    }
  };

  return (
    <div className="App">
      <button onClick={() => setOpen(true)} className="open-modal-btn">
        Начать тест
      </button>

      {open && (
        <div className="overlay">
          <div className="modal">
            <svg
              className="image"
              onClick={() => {
                setOpen(false);
                setCurrentQuestion(0);
                setScore(0);
              }}
              width="18"
              height="20"
              fill="currentColor"
            >
              <use href="/images/delete-icon.svg#trash" />
            </svg>

            <div className="quiz-content">
              <h3>{questions[currentQuestion].question}</h3>
              <div className="options">
                {questions[currentQuestion].options.map((option, index) => (
                  <button
                    key={index}
                    className="option-btn"
                    onClick={() => handleAnswer(index)}
                  >
                    {option}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
