import React from "react";

function App() {
  const [open, setOpen] = React.useState(false);
  const [step, setStep] = React.useState(1);
  const [answers, setAnswers] = React.useState({});

  const handleAnswer = (questionId, answer) => {
    setAnswers(prev => ({ ...prev, [questionId]: answer }));
    
    if (step < 2) {
      setStep(step + 1);
    }
  };

  const resetTest = () => {
    setOpen(false);
    setStep(1);
    setAnswers({});
  };

  return (
    <div className="App">
      <button onClick={() => setOpen(true)} className="open-modal-btn">
        Открыть опрос
      </button>

      <div className={`overlay animated ${open ? "show" : ""}`}>
        <div className="modal">
          <svg
            className="image"
            onClick={resetTest}
            width="18"
            height="20"
            fill="currentColor"
          >
            <use href="/images/delete-icon.svg#trash" />
          </svg>

          <div style={{ textAlign: "center", padding: "20px" }}>
            {step === 1 && (
              <>
                <p style={{ marginBottom: "20px" }}>1. React — это круто?</p>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer(1, "Да")}
                >
                  Да
                </button>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer(1, "Нет")}
                >
                  Нет
                </button>
              </>
            )}
            
            {step === 2 && (
              <>
                <p style={{ marginBottom: "20px" }}>2. Хуки — удобны?</p>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer(2, "Да")}
                >
                  Да
                </button>
                <button 
                  style={btnStyle} 
                  onClick={() => handleAnswer(2, "Нет")}
                >
                  Нет
                </button>
              </>
            )}
            
            {step === 3 && (
              <>
                <h3>Результат:</h3>
                <p>1. React — это круто? - {answers[1] || "Нет ответа"}</p>
                <p>2. Хуки — удобны? - {answers[2] || "Нет ответа"}</p>
                <button 
                  style={{ ...btnStyle, marginTop: "20px" }} 
                  onClick={resetTest}
                >
                  Закрыть
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// Стили кнопок
const btnStyle = {
  padding: "10px 20px",
  margin: "0 5px",
  border: "2px solid #333",
  borderRadius: "5px",
  cursor: "pointer",
  backgroundColor: "#f0f0f0",
  fontSize: "16px"
};

export default App;
